package day01;

public class OpTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int su=2;
		//su = su+2;
		su += 2;
		System.out.println(su);
		
		su -= 1;
		System.out.println(su);
		
		su *= 3;
		System.out.println(su);
		
		su /= 3;
		System.out.println(su);
		
		su %= 3;
		System.out.println(su);
	}

}
