package day01;

public class OpTest3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 20;
		int j = ++i;
		System.out.println(i);
		System.out.println(j);
		
		System.out.println("---------------");
		i = 20;
		j = i++;
		System.out.println(i);
		System.out.println(j);
		
	}

}
