package day01;



public class OpTest4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int su1 = 2;
		int su2;
		su2 = ++su1;
		
		System.out.println(su1 + "," + su2);
		
		su2 = su1--;
		System.out.println(su1 + "," + su2);
		
		su2= --su1;
		System.out.println(su1 + "," + su2);	
	}

}
